import React from "react";
import ProgressBar from "./ProgressBar";
import converterFunc from "./converterFunc";

function WhenPlaying({ focusTime, breakTime, timer, style, isFocusSession, playPauseCounter }) {
  function mode() {
    return (isFocusSession) ? "Focusing" : "Breaking";
  }
  if (playPauseCounter) {
    return (
      <div><div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">
              {mode} for {converterFunc(focusTime)} minutes
            </h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {converterFunc(timer)}
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <ProgressBar style={style} />
            </div>
          </div>
        </div>
      </div>
    );
  } else {
      return <div></div>
  }
}

export default WhenPlaying;
