import React from "react";
import timeChanger from "./timeChanger";
import TimeChanger from "./timeChanger";

function FocusTimer({setFocusTime, setBreakTime, focusTime}) {
  const handleTimeChange = (event) => {
    timeChanger(setBreakTime, setFocusTime, event)
  };
  return (
    <div className="input-group input-group-lg mb-2">
      <span className="input-group-text" data-testid="duration-focus">
        Focus Duration: {focusTime}:00
      </span>
      <div className="input-group-append">
        <button
          type="button"
          name="focus"
          id="decrease"
          value={focusTime}
          onClick={handleTimeChange}
          className="btn btn-secondary"
          data-testid="decrease-focus"
        >
          <span name="focus" id="decrease" className="oi oi-minus" />
        </button>
        <button
          type="button"
          name="focus"
          id="increase"
          value={focusTime}
          onClick={handleTimeChange}
          className="btn btn-secondary"
          data-testid="increase-focus"
        >
          <span name="focus" id="increase" className="oi oi-plus" />
        </button>
      </div>
    </div>
  );
}

export default FocusTimer;
