import React from "react";
import converterFunc from "./converterFunc";

/* 
interval = 300;
max = 3600;
min = 300;
*/

function FocusTimer({setFocusTime, setBreakTime, focusTime}) {
  const handleTimeChange = (event) => {
    const button = event.target
    if (button.value >= 3600 || button.value <= 300){
      return
    }
    (button.id === "increase") ? setFocusTime((current) => current + 300) : setFocusTime((current) => current - 300);
  };
  return (
    <div className="input-group input-group-lg mb-2">
      <span className="input-group-text" data-testid="duration-focus">
        Focus Duration: {converterFunc(focusTime)}
      </span>
      <div className="input-group-append">
        <button
          type="button"
          onClick={handleTimeChange}
          id="decrease"
          value={focusTime}
          className="btn btn-secondary"
          data-testid="decrease-focus"
        >
          <span name="focus" id="decrease" className="oi oi-minus" />
        </button>
        <button
          type="button"
          onClick={handleTimeChange}
          id="increase"
          className="btn btn-secondary"
          data-testid="increase-focus"
        >
          <span name="focus" id="increase" className="oi oi-plus" />
        </button>
      </div>
    </div>
  );
}

export default FocusTimer;
