import React from 'react';
import converterFunc from './converterFunc';

function BreakTimer({breakTime, setBreakTime, setFocusTime}) {
    const handleTimeChange = (event) => {
        let interval;
        let max;
        let min;
        let changeTime;
        // SOMETIMES CONSOLE LOGS BUTTON SOMETIMES SPAN???????????
        console.log(event.target);
        console.log(event.target.name);
        console.log(event.target.id);
        if (event.target.name === "focus") {
          console.log("focus");
          interval = 300;
          max = 3600;
          min = 5;
          changeTime = setFocusTime;
        } else {
          console.log("!focus");
          interval = 60;
          max = 15;
          min = 1;
          changeTime = setBreakTime;
        }
        if (event.target.id === "increase") {
          console.log("increase");
          if (event.target.value >= max){
            changeTime((current) => current - interval);
          };
          changeTime((current) => current + interval);
        } else {
          console.log("!increase");
          if (event.target.value <= min){
            changeTime((current) => current + interval);
          };
          changeTime((current) => current - interval);
        };
    };
    return (
        <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                Break Duration: {converterFunc(breakTime)}
              </span>
              <div className="input-group-append">
                <button
                  type="button"
                  name="break"
                  id="decrease"
                  value={breakTime}
                  onClick={handleTimeChange}
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                >
                  <span name="break" id="decrease" className="oi oi-minus" />
                </button>
                <button
                  type="button"
                  name="break"
                  id="increase"
                  value={breakTime}
                  onClick={handleTimeChange}
                  className="btn btn-secondary"
                  data-testid="increase-break"
                >
                  <span name="break" id="increase" className="oi oi-plus" />
                </button>
              </div>
            </div>
    );
}

export default BreakTimer;
