import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import FocusTimer from "./FocusTimer";
import BreakTimer from "./BreakTimer";
import WhenPlaying from "./WhenPlaying";
import stopButton from "./stopButton";

function Pomodoro() {
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [timer, setTimer] = useState(1500);
  const [focusTime, setFocusTime] = useState(1500);
  const [breakTime, setBreakTime] = useState(300);
  const [style, setStyle] = useState(0);
  const [isFocusSession, setTimerMode] = useState("focus");



  useInterval(
    () => {
      setTimer((current)=> current - 1);
      const stylePercent = 100/timer;
      setStyle((style) => style+stylePercent);


    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <FocusTimer setFocusTime={setFocusTime} setBreakTime={setBreakTime} focusTime={focusTime}/>
        </div>
        <div className="col">
          <div className="float-right">
            <BreakTimer setFocusTime={setFocusTime} setBreakTime={setBreakTime} breakTime={breakTime}/>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              onClick={stopButton(isTimerRunning, setIsTimerRunning, timer, setTimer, setTimerMode, focusTime)}
              className="btn btn-secondary"
              title="Stop the session"
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <WhenPlaying isTimerRunning={isTimerRunning} focusTime={focusTime} timer={timer} style={style} />
    </div>
  );
}

export default Pomodoro;
