function FocusTimer(props) {
  const handleTimeChange = (event) => {
    // attempt:    <TimeChangeButtons event={event} setBreakTime={setBreakTime} setFocusTime={setFocusTime}/>
    let interval;
    let max;
    let min;
    let changeTime;
    // SOMETIMES CONSOLE LOGS BUTTON SOMETIMES SPAN???????????
    console.log(event.target);
    console.log(event.target.name);
    console.log(event.target.id);
    if (event.target.name === "focus") {
      console.log("focus");
      interval = 5;
      max = 60;
      min = 5;
      changeTime = setFocusTime;
    } else {
      console.log("!focus");
      interval = 1;
      max = 15;
      min = 1;
      changeTime = setBreakTime;
    }
    if (event.target.id === "increase") {
      console.log("increase");
      if (event.target.value === max) return;
      changeTime((current) => current + interval);
    } else {
      console.log("!increase");
      if (event.target.value === min) return;
      changeTime((current) => current - interval);
    }

    // still needs ability to stop at max;

    // still needs to be made into a component that...
    // is called instead of existing here;
  };
  return (
    <div className="input-group input-group-lg mb-2">
      <span className="input-group-text" data-testid="duration-focus">
        {/* TODO: Update this text to display the current focus session duration */}
        Focus Duration: {focusTime}:00
      </span>
      <div className="input-group-append">
        {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
        <button
          type="button"
          name="focus"
          id="decrease"
          value={focusTime}
          onClick={handleTimeChange}
          className="btn btn-secondary"
          data-testid="decrease-focus"
        >
          <span className="oi oi-minus" />
        </button>
        {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
        <button
          type="button"
          name="focus"
          id="increase"
          value={focusTime}
          onClick={handleTimeChange}
          className="btn btn-secondary"
          data-testid="increase-focus"
        >
          <span className="oi oi-plus" />
        </button>
      </div>
    </div>
  );
}

export default FocusTimer;
