import React from 'react';
import converterFunc from './converterFunc';

function BreakTimer({breakTime, setBreakTime, setFocusTime}) {
    const handleTimeChange = (event) => {
      const button = event.target;
      (button.id === "increase") ? setBreak((current) => current + 300) : setFocusTime((current) => current - 300);
    };
    return (
        <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                Break Duration: {converterFunc(breakTime)}
              </span>
              <div className="input-group-append">
                <button
                  type="button"
                  name="break"
                  id="decrease"
                  value={breakTime}
                  onClick={handleTimeChange}
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                >
                  <span name="break" id="decrease" className="oi oi-minus" />
                </button>
                <button
                  type="button"
                  name="break"
                  id="increase"
                  value={breakTime}
                  onClick={handleTimeChange}
                  className="btn btn-secondary"
                  data-testid="increase-break"
                >
                  <span name="break" id="increase" className="oi oi-plus" />
                </button>
              </div>
            </div>
    );
}

export default BreakTimer;
