import React from 'react';

function sessionLoop( timer, setTimer, isFocusSession, setIsFocusSession, breakTime, focusTime) {
    if (timer === 0){
        // sound alarm
        if (isFocusSession) {
            setTimer(() => breakTime);
        } else {
            setTimer(() => focusTime);
        }
        setIsFocusSession((prev) => !prev)
    }
}

export default sessionLoop;
