import React from 'react';

function sessionLoop(timer, setTimer, isFocusSession, setIsFocusSession, breakTime, focusTime, setStyle) {
    if (timer === 0){
        new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
        if (isFocusSession) {
            setTimer(() => breakTime);
        } else {
            setTimer(() => focusTime);
        }
        setIsFocusSession((prev) => !prev)
        setStyle(() => 0)
    }
}

export default sessionLoop;
