import React from 'react';

function stopButton(setIsTimerRunning, setTimer, focusTime) {
    // set isTimerRunning to false
    // set timer to focusTime
    if (isTimerRunning) {}
    setIsTimerRunning(false);
    setTimer(focusTime);
}

export default stopButton;
