import React from "react";
import classNames from "../utils/class-names";

function PlayPauseStop({
  isTimerRunning,
  setIsTimerRunning,
  timer,
  focusTime,
  breakTime,
  setTimer,
  isFocusSession,
  setIsFocusSession,
  setStyle,
  playPauseCounter,
  setPlayPauseCounter
}) {
  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
    setPlayPauseCounter((current) => current+1);
  }
  function stopButton() {
    const checkSession = isFocusSession ? focusTime : breakTime;
    if (playPauseCounter) {
      setIsTimerRunning(() => false);
      setTimer((current) => focusTime);
      setIsFocusSession(() => true);
      setStyle((current) => current - current);
      setPlayPauseCounter(() => 0);
    }
  }
  return (
    <div
      className="btn-group btn-group-lg mb-2"
      role="group"
      aria-label="Timer controls"
    >
      <button
        type="button"
        className="btn btn-primary"
        data-testid="play-pause"
        title="Start or pause timer"
        onClick={playPause}
      >
        <span
          className={classNames({
            oi: true,
            "oi-media-play": !isTimerRunning,
            "oi-media-pause": isTimerRunning,
          })}
        />
      </button>
      {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
      <button
        type="button"
        onClick={stopButton}
        className="btn btn-secondary"
        title="Stop the session"
      >
        <span className="oi oi-media-stop" />
      </button>
    </div>
  );
}

export default PlayPauseStop;
