import React from 'react';

function stopButton(isTimerRunning, setIsTimerRunning, setTimer, setTimerMode, focusTime) {
    // set isTimerRunning to false
    // set timer to focusTime
    if (isTimerRunning && timer < focusTime) {
        setIsTimerRunning(false);
        setTimer(focusTime);
        setTimerMode("focus");
    } else {
        return
    };
}

export default stopButton;
